import { BsTelephoneFill } from "react-icons/bs";
import { FaUserCircle } from "react-icons/fa";
import { MdEmail } from "react-icons/md";
import Slide from "react-reveal/Slide";
import Fade from "react-reveal/Fade";
import "./amazon-research-banner.scss";
import Navbar from "./../../../../components/navbar/navbar";
import Button from "./../../../../components/common/button/button";
import { useEffect, useState } from "react";
import { client } from "./../../../../client";

const AmazonResearchBanner = () => {
  const [ARBanner, setARBanner] = useState();

  useEffect(() => {
    client
      .getEntries({
        content_type: "amazonResearchBanner",
        select: "fields",
      })
      .then((res) => {
        setARBanner(res.items[0].fields);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div className="creative-banner ">
      <Navbar />

      <div className="headings container">
        <div className="row">
          <Slide left duration={1200}>
            <div className="col-xl-6 col-lg-6 col-md-6 col-sm-12 col-xs-12 heading-coloumn">
              <h3>{ARBanner && ARBanner.subHeading}</h3>

              <h1 className="over-write">{ARBanner && ARBanner.mainHeading}</h1>
            </div>
          </Slide>
          <div className="col-xl-6 col-lg-6 col-md-6 col-sm-12 col-xs-12  form-coloumn">
            <Fade duration={2000} delay={300} big>
              <div className="form">
                <h1>Grow Your Brand</h1>
                <h6>by partnering with SmartSites</h6>
                <hr />
                <form>
                  <FaUserCircle className="fa-circle" style={{ top: 30 }} />{" "}
                  <input placeholder="Full Name" type="text" />
                  <MdEmail className="md-email" style={{ top: 107 }} />
                  <input placeholder="Email Adress" type="text" />
                  <BsTelephoneFill
                    className="bs-telephone"
                    style={{ top: 184 }}
                  />
                  <input placeholder="Phone Number" type="text" />
                  <Button
                    label="Get Your Free Proposal"
                    style={{
                      borderRadius: "10px",
                      background: "#669021",
                      color: "white",
                      outline: "none",
                      border: "1px solid #66902f",
                      padding: "10px 18px",
                      marginTop: "35px",
                      marginBottom: "35px",
                      fontSize: "18px",
                      height: "-moz-fit-content",
                      height: "fit-content",
                    }}
                  />
                </form>
              </div>
            </Fade>
          </div>
        </div>
        <br />
        <br />
      </div>
    </div>
  );
};

export default AmazonResearchBanner;
