import { createSlice } from "@reduxjs/toolkit";

export const dataSlice = createSlice({
  name: "Data",
  initialState: {
    data: null,
  },
  reducers: {
    addData: {
      reducer: (state, action) => {
        state.data = action.payload;
      },
    },
  },
});

export const { addData } = dataSlice.actions;
export default dataSlice.reducer;
